// Calculate comprehensive test statistics
const originalStdout = $json.stdout || '{}';
let testReports = {};

try {
  testReports = JSON.parse(originalStdout);
} catch(e) {
  console.log('Failed to parse stdout:', e.message);
  testReports = {};
}

const groups = ['agents','workflows','context','knowledge','playbooks','settings','analytics'];
let totalTests = 0, totalPassed = 0;
const moduleStats = [];
const allErrors = [];

// Process each module
for (const group of groups) {
  const report = testReports[group];
  if (!report || report.ok === false) {
    moduleStats.push({module: group, status: 'missing', error: 'No report found'});
    continue;
  }

  if (report.summary) {
    const summary = report.summary;
    const moduleTests = summary.total_tests || 0;
    const modulePassed = summary.passed || 0;
    const moduleRate = summary.success_rate || 0;

    totalTests += moduleTests;
    totalPassed += modulePassed;

    moduleStats.push({
      module: group,
      tests: moduleTests,
      passed: modulePassed,
      failed: summary.failed || 0,
      success_rate: moduleRate,
      status: summary.status
    });

    // Extract specific errors from detailed_results
    if (report.detailed_results) {
      for (const suite of report.detailed_results) {
        if (suite.results) {
          for (const test of suite.results) {
            if (test.status === 'failed') {
              allErrors.push({
                module: group,
                suite: suite.test_suite,
                test: test.name,
                error: test.error,
                duration: test.duration
              });
            }
          }
        }
      }
    }
  }
}

const overallSuccessRate = totalTests > 0 ? (totalPassed / totalTests) * 100 : 0;
const needsAnalysis = overallSuccessRate < 85 || allErrors.length > 0;

// Categorize errors by type
const errorTypes = {
  missing_endpoints: allErrors.filter(e => e.error && e.error.includes('404')),
  validation_errors: allErrors.filter(e => e.error && e.error.includes('422')),
  method_errors: allErrors.filter(e => e.error && e.error.includes('405')),
  data_format_errors: allErrors.filter(e => e.error && (e.error.includes("'data'") || e.error.includes('data field'))),
  security_errors: allErrors.filter(e => e.error && e.error.includes('401') || e.error.includes('403') || e.error.includes('Expected 401/403')),
  other_errors: allErrors.filter(e => e.error && !e.error.match(/404|422|405|'data'|data field|401|403/))
};

console.log(`Analysis Complete: ${totalPassed}/${totalTests} tests passed (${overallSuccessRate.toFixed(1)}%)`);
console.log(`Error Breakdown: 404=${errorTypes.missing_endpoints.length}, 422=${errorTypes.validation_errors.length}, 405=${errorTypes.method_errors.length}, data=${errorTypes.data_format_errors.length}, security=${errorTypes.security_errors.length}, other=${errorTypes.other_errors.length}`);

return [{
  json: {
    summary: {
      totalTests,
      totalPassed,
      totalFailed: totalTests - totalPassed,
      overallSuccessRate,
      needsAnalysis
    },
    moduleStats,
    errorAnalysis: {
      totalErrors: allErrors.length,
      errorTypes,
      priorityOrder: [
        {type: 'missing_endpoints', count: errorTypes.missing_endpoints.length, priority: 1},
        {type: 'validation_errors', count: errorTypes.validation_errors.length, priority: 2},
        {type: 'data_format_errors', count: errorTypes.data_format_errors.length, priority: 3},
        {type: 'method_errors', count: errorTypes.method_errors.length, priority: 4},
        {type: 'security_errors', count: errorTypes.security_errors.length, priority: 5},
        {type: 'other_errors', count: errorTypes.other_errors.length, priority: 6}
      ]
    },
    rawTestData: testReports,
    sessionId: `multi-agent-${Date.now()}-${Math.random().toString(36).substr(2, 8)}`
  }
}];
